mapper.select:
SELECT t.* FROM mf_good_price t 
WHERE 1=1 

 	AND t.id=#{id}


 	AND t.goodId=#{goodId}


 	AND t.natureId=#{natureId}

ORDER BY t.createTime ASC

mapper.delete:
	DELETE FROM mf_good_price  
	WHERE 

        id=#{id}


        goodId=#{goodId}

DELETE from mf_good_price 
WHERE id in(

		#{o.id}

	)
